pragma solidity ^0.4.0;
contract MedicalRecord {

    struct Record {
        string link;
        bool isEntity;
        string _hash;
    }
    mapping(address => Record) private db;
    function storeRecord(string link, string _hash) public returns(bool success) {
        require(!db[msg.sender].isEntity);
        db[msg.sender].link = link;
        db[msg.sender]._hash = _hash;
        db[msg.sender].isEntity = true;
        RecordStored();
        return true;
    }
    function updateRecord(string newLink, string newHash) public returns(bool success) {
        require(db[msg.sender].isEntity);
        db[msg.sender].link = newLink;
        db[msg.sender]._hash = newHash;
        RecordUpdated();
        return true;
    }
    function deleteRecord() public returns(bool success) {
        require(db[msg.sender].isEntity);
        db[msg.sender].isEntity = false;
        return true;
    }
    function getLink() view public returns(string) {
        require(db[msg.sender].isEntity);
        return db[msg.sender].link;
    }
    function getHash() view public returns(string) {
        require(db[msg.sender].isEntity);
        return db[msg.sender]._hash;
    }
    event RecordUpdated();
    event RecordStored();
}
